    public function getSourceContext(): Source
    {
        return new Source("{% extends 'base.html.twig' %}

{% block title %}{{ title }}{% endblock %}

{% block body %}
<div class=\"container mt-4\">
    <div class=\"row justify-content-center\">
        <div class=\"col-md-8\">
            <div class=\"card\">
                <div class=\"card-header bg-primary text-white\">{{ title }}</div>
                <div class=\"card-body\">
                    {{ form_start(form, {attr: {novalidate: 'novalidate'}}) }}
                    {{ form_widget(form) }}
                    <button class=\"btn btn-primary mt-3\">Enregistrer</button>
                    {{ form_end(form) }}
                </div>
            </div>
        </div>
    </div>
</div>
{% endblock %}

{% block javascripts %}
<script>
document.addEventListener('DOMContentLoaded', function() {
    // Disable HTML5 validation for form
    const form = document.querySelector('form');
    if (form) {
        form.setAttribute('novalidate', 'novalidate');
        
        // Prevent default validation on submit
        form.addEventListener('submit', function(event) {
            if (!form.checkValidity()) {
                event.preventDefault();
                event.stopPropagation();
            }
        }, false);
        
        console.log('HTML5 validation disabled for form');
    }
});
</script>
{% endblock %}", "dashboard/user_form.html.twig", "C:\\Users\\rajhi\\pidev-education-app\\templates\\dashboard\\user_form.html.twig");
